SELECT DISTINCT TOP(1) tb.* FROM title_basics AS tb INNER JOIN title_ratings AS tr ON tb.titleID = tr.titleID WHERE rating > 6 AND NOT EXISTS ( SELECT * FROM title_crew WHERE directorId IN (SELECT directorId from title_crews where filmID in ('Die hard', 'Love actually') ) OR writerId IN (SELECT writerId from title_writers where filmID='Love actually')) GROUP BY tb.titleID ORDER by avgrating DESC LIMIT 1