SELECT t.titleID as id, t.titleType as type, t.primaryTitle as pname, t.originalTitle as otitle, t.isAdult as adult, t.startYear as yearStart, t.endYear as yearEnd, t.runtimeMinutes as runTimeMins, g.genreIDs as genreIDS from title_basics t inner join title_rating r on t.titleID = r.titleID left outer join name_basics n on t.titleID = n.titleID left outer join crew c on t.titleID=c.titleID where t.titleType='Movie' order by rating DESC limit 6