SELECT * FROM (title_ratings INNER JOIN (name_basics INNER JOIN title_crew ON (name_basics.primaryName = title_crew.director)) WHERE title_ratings.numVotes >= 1 AND ((title_ratings.averageRating > 6 OR title_ratings.averageRating < 9) AND (title_ratings.averageRating <= 6 OR title_ratings.averageRating >= 9))) UNION ALL SELECT * FROM (title_rating INNER JOIN (title_principals INNER JOIN (name_basics INNER JOIN title_crew ON (name_basics.primaryName = title_crew.writer))) ON (title_ratting.movieId = title_principal.id) WHERE title_ratings.avarageRating >= 7)