SELECT DISTINCT t.titleId AS id, t.titleType as type FROM title_basics t JOIN title_rating r ON t.titleId = r.titleID WHERE r.averageRating > 6 AND NOT EXISTS( SELECT * FROM title_crew c WHERE c.directorId IN (select d.id from director_ratings dr where dr.movieId=r.titleId)) OR exists( select * from crew_members cm where cm.actorId in (select p.characterId from principal_casts pc where pc.movieId=r.titleId))