SELECT * FROM (title_basics JOIN (SELECT DISTINCT(tconst)) AS tb ON title_basics.tconst = tb.tconst WHERE ((title_basics.genre LIKE '%drama%' OR title_basics.primaryGenre IS NULL) AND title_basics.startYear >= '1970' AND title_basics.endYear <= CURRENT YEAR - 1 ) UNION SELECT * FROM (title_principals LEFT OUTER JOIN (SELECT DISTINCT(nconst)) AS np ON title_principals.nconst = np.nconst WHERE (((title_principals.category IN ('Actor', 'Actress')) AND title_principals.job NOT IN('Director') AND title_principals.character NOT IN ('Stunt Double'))))) UNION SELECT * from (name_basic LEFT outer join (select distinct year) as yr ON name_base.year = yr.yr) where name_basics.primaryName in ('Will Smith','Jaden Smith'))