SELECT movies.* FROM title_ratings AS ratings INNER JOIN title_crew AS crew ON titles.id = crew.title_id AND crew.job IN ('director') WHERE rating >= 6.5 AND id NOT IN( SELECT titlestatistics.movie_id FROM title_statistics as statistic LEFT OUTER JOIN title_ratings as rating USING (id) where (titlestatistics.rating >= 7 OR titlestatistics.rating <= 1)) ORDER BY rating DESC LIMIT 10