SELECT * FROM ( SELECT DISTINCT tc.*, nc.*, tr.*, tcr.* WHERE tc.title_type = "movie" AND tc.isadult > 0 ) AS m JOIN ( SELECT d.* FROM director as d WHERE d.director_name IN ('Glenn Ficarra') OR d.director_name IN('John Requa'))as dr ON(dr.director_name=m.director) JOIN ( select w.* from writer as w where w.writer_name in ("Dan Fogelman") or w.writer_name="Nick Schenk" or w.writer_name="Steve Martin") as wr ON(wr.writer_name=m.writer) join (select c.*from crew as c where c.job='Director'and c.category='Directing') as dir ON (dir.job=m.director)