SELECT title_recommendation.* FROM (title_recommendation JOIN title_basics ON title_basics.titleID = title_recommendation.movieID WHERE title_basics.isAdult=false AND ((((title_rating.averageRating>=6))) OR ((title_crew.director="Mike Flanagan"))) ) AND ((((title_principal.job="Actress") AND (title_principal.category="Horror")))) AND ((title_recommendation.primaryName LIKE "Blair%")) ORDER BY RAND() LIMIT 1