SELECT * FROM ( SELECT t.*, r.*, u.*, v.* from tconst AS t INNER JOIN name_basics AS u ON t.nconst = u.primaryName AND t.isAdult=u.birthYear>=1900 AND t.startYear<=u.deathYear AND u.job IN ('actor','director') WHERE u.knownfortitles LIKE '%happy gilmore%' ) LEFT OUTER JOIN title_rated AS r ON t.tconst=r.tconst LEFT OUTER JOIN title_principal AS p ON r.tconst=p.tconst LEFT OUTER JOIN title_voted AS v ON p.ordering=v.ordering AND r.averagerating>='4.0' ORDER BY r.numvote DESC LIMIT 5 OFFSET 0 