SELECT * FROM ((SELECT DISTINCT movietype AS genre, COUNT(titleid) AS ratingcount FROM title WHERE titletype = "comedy") AS ratedmovies INNER JOIN (SELECT DISTINCT titleid AS tID, AVG((ratingvalue)) AS avgRating FROM ratings GROUP BY titleid HAVING count > 1 AND avgrating <.75 ) AS highrated ON ratedmovies.titleid=highrated.tID LEFT OUTER JOIN (SELECT DISTINCT titleid, MIN(ordering), MAX(job) AS maxJob, PRIMARYKEY AS id FROM cast GROUP BY titleid) AS cast ON ratedmovies.movieid=cast.titleid ORDER BY castingorder DESC LIMIT 1