SELECT TOP 10 titles.* FROM ( SELECT tc.*, nr.* from title_ratings as r INNER JOIN title_crew as c ON r.director = c.id WHERE r.averageRating > 7 AND r.numVotes >= 900 ) AS subquery LEFT OUTER JOIN title_principals AS p ON SUBQUERY.title = p.primaryTitle OR SUBQUERY.title = p.originalTitle LEFT OUTER JOIN title_basic AS bb ON P.primaryName = BB.primaryName LEFT OUTER JOIN name_basic AS nb ON BB.birthYear = NB.birthYear LEFT OUTER JOIN name_basic AS db ON BB.deathYear = DB.deathYear where (bb.job='actor' OR bb.job= 'actress') ORDER BY SUBQ.averageRATING DESC LIMIT 10