SELECT * FROM (SELECT t.titleID AS ID, tb.* FROM titles AS t INNER JOIN title_basics as tb ON t.titleID = tb.titleID AND t.isAdult=false WHERE t.primaryTitle='Bad Moms' OR t.originalTitle='Bad Moms') AS tb LEFT OUTER JOIN( SELECT n.nameID AS NID, nb.* FROM names AS n INNER JOIN name_basics as nb ON n.nameID = nb.nameID WHERE n.category='Actresses') AS n ON tb.primaryName = n.NID AND n.ordering < 999 ORDER BY rating DESC LIMIT 1